Statements.Statement:
INSERT INTO `soodal`.`tb_exchangerate`
(`ExDate`,
`CurrCode`,
`CurrName`,
`CashBuy`,
`CashBuySpread`,
`CashSale`,
`CashSaleSpread`,
`RemitSend`,
`RemitRecv`,
`TCBuy`,
`Check`,
`BasicRate`,
`Commission`,
`Convert`)
VALUES
(@ExDate,
@CurrCode,
@CurrName,
@CashBuy,
@CashBuySpread,
@CashSale,
@CashSaleSpread,
@RemitSend,
@RemitRecv,
@TCBuy,
@Check,
@BasicRate,
@Commission,
@Convert)
ON DUPLICATE KEY UPDATE
`CurrName` = @CurrName,
`CashBuy` = @CashBuy,
`CashBuySpread` = @CashBuySpread,
`CashSale` = @CashSale,
`CashSaleSpread` = @CashSaleSpread,
`RemitSend` = @RemitSend,
`RemitRecv` = @RemitRecv,
`TCBuy` = @TCBuy,
`Check` = @Check,
`BasicRate` = @BasicRate,
`Commission` = @Commission,
`Convert` = @Convert
;
