Statements.Statement:
INSERT INTO TB_CODEMASTER
(
`Code`,
`Name`,
`Remark`,
`UseYn`,
`CreateId`,
`CreateDt`
)
VALUES
(
@Code,
@Name,
@Remark,
@UseYn,
@CreateId,
now()
)
ON DUPLICATE KEY UPDATE
`Name` = @Name,
`Remark` = @Remark,
`UseYn` = @UseYn,
`UpdateId` = 'SYSTEM',
`UpdateDt` = now(),
`DelYn` = 'N',
`DeleteId` = 'SYSTEM',
`DeleteDt` = null
;

SELECT IFNULL(@CODE,'') AS Code;
